%needs frequecny input here 
appl.var = {'nu','freq'};
fem.appl{1} = appl;
fem.frame = {'ref'};
fem.border = 1;
clear units;
units.basesystem = 'SI';
fem.units = units;



% ODE Settings
clear ode
clear units;
units.basesystem = 'SI';
ode.units = units;
fem.ode=ode;
% Multiphysics
fem=multiphysics(fem);



% Extend mesh
fem.xmesh=meshextend(fem);

% Solve problem
fem.sol=femstatic(fem, ...
                  'complexfun','on', ...
                  'solcomp',{'Ez'}, ...
                  'outcomp',{'Ez'}, ...
                  'pname','freq', ...
                  'plist',freq*3e8, ...
                  'oldcomp',{});

% Plot solution
postplot(fem, ...
         'tridata',{'Ez','cont','internal','unit','V/m'}, ...
         'tridlim',[-2500 2500], ...
         'trimap','wavemap(1024)', ...
         'title','freq', ...
         'axis',[-1.5,31.575,-5.523863636363636,15.523863636363636]);
     
%end






